mapper.sql:
        WHERE DELETED_FLAG=0

            AND b_post_comment.id = #{cond.id}


            AND b_post_comment.user_id = #{cond.userId}


            AND b_post_comment.post_id = #{cond.postId}     


            AND b_post_comment.content = #{cond.content}     


            AND b_post_comment.deleted_flag = #{cond.deletedFlag}     


            AND b_post_comment.version = #{cond.version}


            and b_post_comment.id in

                #{item}




			AND b_post_comment.CREATED_DATE >= #{cond.createdDateB}




			AND b_post_comment.CREATED_DATE <= #{cond.createdDateE}




			AND b_post_comment.UPDATED_DATE >= #{cond.updatedDateB}




			AND b_post_comment.UPDATED_DATE <= #{cond.updatedDateE}

mapper.update:
update b_post_comment
id = #{id}, user_id = #{userId}, post_id = #{postId}, content = #{content}, deleted_flag = #{deletedFlag}, version = version + 1, UPDATED_DATE = current_timestamp(), UPDATED_BY=#{updatedBy}
where b_post_comment.id = #{id} and b_post_comment.version = #{version}
